package com.expression;

import org.junit.Test;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertThrows;

public class ExpressionConverterTest {

    @Test
    public void testParserWhenAddFunctionInputShouldSuccess() {
        String result = new ExpressionConverter().toPostFix("(add 1 1)".split(" "));
        assertEquals("11+", result);
    }

    @Test
    public void testParserWhenNestedFunctionInputShouldSuccess() {
        String result = new ExpressionConverter().toPostFix("(multiply 3 (multiply (multiply 3 3) 3))".split(" "));
        assertEquals("333*3**", result);
    }

    @Test
    public void testParserWhenNestedMixedFunctionInputShouldSuccess() {
        String result = new ExpressionConverter().toPostFix("(multiply 3 (add (multiply 3 3) 3))".split(" "));
        assertEquals("333*3+*", result);
    }

    @Test
    public void testParserWhenInputUpperAndLowerShouldSuccess() {
        String result = new ExpressionConverter().toPostFix("(add 3 (Add (MULTIPLY 3 3) 3))".split(" "));
        assertEquals("333*3++", result);
    }

    @Test
    public void testParserWhenIntegerInputShouldSuccess() {
        String result = new ExpressionConverter().toPostFix("123".split(" "));
        assertEquals("123", result);
    }

    @Test
    public void testParserWhenNestedAddShouldSuccess() {
        String result = new ExpressionConverter().toPostFix("add (add 11 1) 1".split(" "));
        assertEquals("111+1+", result);
    }

    @Test
    public void testParserWhenInvalidFunctionShouldThrow() {
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class,
            () -> new ExpressionConverter().toPostFix("ADD1".split(" ")));
        assertEquals("Invalid expression: [ADD1].", exception.getMessage());
    }

    @Test
    public void testParserWhenMissingOpenParenthesisShouldThrow() {
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class,
            () -> new ExpressionConverter().toPostFix("ADD 1 1)".split(" ")));
        assertEquals("Unbalanced parenthesis.", exception.getMessage());
    }

    @Test
    public void testParserWhenMissingInnerOpenParenthesisShouldThrow() {
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class,
            () -> new ExpressionConverter().toPostFix("add (add 11 1 1".split(" ")));
        assertEquals("Unbalanced parenthesis.", exception.getMessage());
    }

    @Test
    public void testParserWhenMissingCloseParenthesisShouldThrow() {
        String base = "(add 0 x)";
        String longExpression = base.replace("x", base);

        for (int i = 0; i <= 10000; i++) {
            longExpression = base.replace("x", longExpression);

        }
        longExpression = longExpression.replace("x", "1");

        String result = new ExpressionConverter().toPostFix(longExpression.split(" "));

        System.out.println(result);

        System.out.println(new ExpressionEvaluator().evaluatePostfix(result));

        assertEquals(
            "",
            result);
    }

}