package com.expression;

import org.junit.Test;

import static org.junit.Assert.*;

public class ExpressionEvaluatorTest {

    @Test
    public void testEvaluatePostfixWhenAddShouldSuccess() {
        int result = new ExpressionEvaluator().evaluatePostfix("11+");
        assertEquals(2, result);
    }

    @Test
    public void testEvaluatePostfixWhenMultiplyShouldSuccess() {
        int result = new ExpressionEvaluator().evaluatePostfix("11*");
        assertEquals(1, result);
    }

    @Test
    public void testEvaluatePostfixWhenNestedMultiplyShouldSuccess() {
        int result = new ExpressionEvaluator().evaluatePostfix("333*3**");
        assertEquals(81, result);
    }

    @Test
    public void testEvaluatePostfixWhenNestedMixShouldSuccess() {
        int result = new ExpressionEvaluator().evaluatePostfix("333*3+*");
        assertEquals(36, result);
    }

    @Test
    public void testEvaluatePostfixWhenLongExpressionShouldSuccess() {
        int result = new ExpressionEvaluator().evaluatePostfix(
            "");
        assertEquals(1, result);
    }

    @Test
    public void testEvaluatePostfixWhenIntegerShouldSuccess() {
        int result = new ExpressionEvaluator().evaluatePostfix("123");
        assertEquals(123, result);
    }

    @Test
    public void testEvaluatePostfixWhenEmptyShouldSuccess() {
        int result = new ExpressionEvaluator().evaluatePostfix("");
        assertEquals(0, result);
    }

    @Test
    public void testEvaluatePostfixWhenInsufficientTermsShouldThrow() {
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class,
            () -> new ExpressionEvaluator().evaluatePostfix("+"));

        assertEquals("Insufficient terms to evaluate the expression", exception.getMessage());
    }

    @Test
    public void testEvaluatePostfixWhenInvalidTermShouldThrow() {
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class,
            () -> new ExpressionEvaluator().evaluatePostfix("11p"));

        assertEquals("Invalid expression", exception.getMessage());
    }
}